gulpfile.js:
```javascript
// 
const gulp=require('gulp');
const  uglify=require('gulp-uglify'); //js
const  concat=require('gulp-concat'); //
const jshint = require('gulp-jshint'); //js
const rename = require('gulp-rename'); // 
const sass = require('gulp-sass'); // scss
const  minifycss = require('gulp-minify-css'); // css
// const livereload = require('gulp-livereload'); // 
const  del = require('del'); //
const connect = require('gulp-connect'); // 

gulp.task('server', function() {
  connect.server({
    port: 8080, //localhost:8080html
    root: './', //html
    livereload: true //connect.reload()
  });
});

//  "my-task" js
gulp.task('my-task-js', function(){
  gulp.src('./js/*.js')
    .pipe(jshint())
    .pipe(uglify())
    .pipe(concat('all.js'))
    .pipe(rename({suffix: '.min'}))
    .pipe(gulp.dest('./dist/js'))
    .pipe(connect.reload())
});


//  "my-task-css" scsscss
gulp.task('my-task-css', function() {
  gulp.src('./css/*.scss')
    .pipe(sass().on('error', sass.logError))
    .pipe(concat('all.css'))
    .pipe(rename({suffix: '.min'}))
    .pipe(minifycss())
    .pipe(connect.reload())
    .pipe(gulp.dest('./dist/css'))

});

gulp.task('html', function(){
  gulp.src('*.html')
    .pipe(gulp.dest('dist/html'))
    .pipe(connect.reload())
})
//
gulp.task('clean', function() {
  return del(['./dist/css/all.css', './dist/css/all.min.css', './dist/all.js','./dist/all.min.js', './dist/html'])
});
// 

gulp.task('default',['clean'],function() {
  gulp.start('my-task-js', 'my-task-css', 'watch', 'server' );
});
// 
gulp.task('watch', function() {
  // Watch.js files
  gulp.watch('./js/*.js', ['my-task-js']);
  // Watch .scss files
  gulp.watch('./css/*.scss', ['my-task-css']);
  // Watch .html files
  gulp.watch('./*.html', ['html']);
  // Watch any files in dist/, reload on change
  // gulp.watch(['dist/!**']).on('change', livereload.changed);
});

```
